function changeLocale(e){var t=getBackUrlString();window.location.href="/main/locale?backUrl="+t+"&locale="+e}function setAdminLocale(){window.location.href="/main/locale?backUrl=&locale=en"}function getBackUrlString(){if(document.getElementById("mainForm")!=null&&document.getElementById("main_site")!=null){return document.mainForm.currentUri.value}return encodeURIComponent(window.location.href)}